int endTime = 5000;
int startTime = 0;
int atm = 0;
int n = 0;
int flag = 1;


void setup() {
  //DEMO 32x64
  runner.init();
  runner.addTask(display_ticker);
  //Text
  runner.addTask(scrollText32x64);
  runner.addTask(showText64);
  //Bitmap
  runner.addTask(showBlueKid);
  runner.addTask(showGBA);  
  runner.addTask(showBlockcraft);
  runner.addTask(showSunset);
  //Testing
  runner.addTask(drawSomePixels);
  runner.addTask(drawSomeLines);
  runner.addTask(colorTesting);

  /* DEMO 32x128 BUGGED
  runner.addTask(scrollText32x128);
  runner.addTask(showText128);
  */
  display_ticker.enable();
}

void loop() {
  runner.execute();
  atm = millis();
  if(atm - startTime > 8000){
    startTime = atm;
    n++;
    if(n>7) n=0;
    flag = 1;
  }

  if(n==0 && flag){
    drawSomePixels.disable();
    led.clearDisplay();
    showText64.restart();
    flag = 0;
  }  
  if(n==1 && flag){
    showText64.disable();
    led.clearDisplay();
    scrollText32x64.restart();
    flag = 0;
  }
  if(n==2 && flag){
    scrollText32x64.disable();
    led.clearDisplay();
    showBlueKid.restart();
    flag = 0;
  }
  if(n==3 && flag){
    showBlueKid.disable();
    led.clearDisplay();
    showGBA.restart();
    flag = 0; 
  }
  if(n==4 && flag){
    showGBA.disable();
    led.clearDisplay();
    showBlockcraft.restart();
    flag = 0; 
  }
  if(n==5 && flag){
    showBlockcraft.disable();
    led.clearDisplay();
    showSunset.restart();
    flag = 0; 
  }
  if(n==6 && flag){
    showSunset.disable();
    led.clearDisplay();
    drawSomePixels.restart();
    flag = 0; 
  }
  if(n==7 && flag){
    drawSomePixels.disable();
    led.clearDisplay();
    drawSomeLines.restart();
    flag = 0; 
  }  
  if(n==8 && flag){
    drawSomePixels.disable();
    led.clearDisplay();
    colorTesting.restart();
    flag = 0; 
  }
}